.data
	words: .asciiz ""
	numWords: .word 674
.text
	lw	$a1, numWords	# Upperbound for random number generation
	li	$v0, 42		# Syscall for generating random number
	syscall			# Generate random number, stored in $a0
	sll	$a0, $a0, 2	# Multiply by 4 because each word is 4 bytes long
	la	$t2, words	# $t2 contains start address of words data segment
	add	$t2, $t2, $a0	# Add word offset to make $t2 store position of start of word
	lw	$t2, ($t2)	# Load the reverse word into $t2
	li	$t0, 0xFF	# Mask for extracting characters
flipString:
	sll	$s0, $s0, 8	# Move on to next digit
	and	$t1, $t2, $t0	# Get first (which is really last) character out of $s0 and store into $t1
	or	$s0, $s0, $t1	# Write digit into $s0
	srl	$t2, $t2, 8	# Move on to next digit
	bne	$t2, $zero, flipString # Repeat process for every digit
	
	# At this point, the only register that needs to stay the same is $s0, which stores the word that was retrieved from the list of words
	
	
	